    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
try
{
    Class.forName("java.sql.Driver");
    Connection c=DriverManager.getConnection("jdbc:mysql://localhost:3306/dbmsproject","root","");
    Statement s1=c.createStatement();
    String name=t1.getText(),user=t2.getText(), pass=p2.getText(),address=t3.getText(), email=t4.getText(),day=t5.getText(),month=t6.getText(),year=t7.getText(),gen="0";

   String bd=year+month+day;

    if(r1.isSelected()==true)
    {
        gen="m";
    }
    else if(r3.isSelected()==true)
    {
        gen="f";
    }
   else if(r2.isSelected()==true)
    {
        gen="o";
    }
    String x="Insert into customer (name,username,password,address,email,birthdate,gender) values('"+(name)+"','"+(user)+"','"+(pass)+"','"+(address)+"','"+(email)+"','"+(bd)+"','"+(gen)+"')";
    s1.executeUpdate(x);
    String sql="Select custid from customer where username='"+(user)+"' and name='"+(name)+"'";
    ResultSet rs=s1.executeQuery(sql);
    int custid=0;
    while(rs.next())
    {
        custid=rs.getInt(1);
    }
    
    JOptionPane.showMessageDialog(this,"Account Successfully Created");
    s1.close();c.close();
    dispose();
    new ShoppingWindow(custid).setVisible(true);
    
    
}
catch(Exception e)
{
    JOptionPane.showMessageDialog(this, e.getMessage());
}

// TODO add your handling code here:
    }//GEN-LAST:event_jButton2ActionPerformed

