    private void p2FocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_p2FocusLost
        String pass=p2.getText();
        if(!((pass.length()>=8) && (pass.length()<=15))){
            l1.setText("Password should be between 8 to 15 characters");
            p2.requestFocus();
        }
        else if(pass.contains(" ")){
            l1.setText("Password shouldn't contain any space");
            p2.requestFocus();
        }
        else
        {
            int cnum=0,clow=0,cup=0;
            for(int i=0;i<10;i++){
                String str=Integer.toString(i);
                if(pass.contains(str)){
                    cnum=1;
                    break;
                }
            }
            for(int i=65;i<=90;i++){
                char c=(char)i;
                String str=Character.toString(c);
                if(pass.contains(str)){
                    cup=1;
                    break;
                }
            }
            for(int i=90;i<=122;i++){
                char c=(char)i;
                String str=Character.toString(c);
                if(pass.contains(str)){
                    clow=1;
                    break;
                }
            }
            if(cnum==0)
            {
                l1.setText("Password should have digits");
                p2.requestFocus();
            }
            else if(clow==0)
            {
                l1.setText("Password should have lowercase characters");
                p2.requestFocus();
            }
            else if(cup==0)
            {
                l1.setText("Password should have uppercase characters");
                p2.requestFocus();
            }
            else
            {
                l1.setText("");
            }
        }

        
        // TODO add your handling code here:
}//GEN-LAST:event_p2FocusLost

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
  String user=u1.getText();

  String pass=p1.getText();
  if((pass.compareTo("")==0)|| (user.compareTo("")==0)){
      l3.setText("Please Enter Username And Password Fields");
  }
  else if((pass.compareTo("")!=0) && (user.compareTo("")!=0)){
  String sql="Select custid,name,username,password from customer";

  try{
      Class.forName("java.sql.Driver");
      Connection c=DriverManager.getConnection("jdbc:mysql://localhost:3306/dbmsproject","root","");
      Statement s=c.createStatement();
      ResultSet rs=s.executeQuery(sql);
      String n="",pd="",v="",username="";
      int custid=0;
      while(rs.next())
      {
          custid=rs.getInt(1);
          v=rs.getString(2);
          n=v.toLowerCase();
          username=rs.getString(3);
          pd=rs.getString(4);
       if((username.equals(user))&& (pd.equals(pass)))
      {
          JOptionPane.showMessageDialog(this,"Welcome");
          dispose();
          new ShoppingWindow(custid).setVisible(true);
      }
      else{
           l3.setText("The entered password or username is wrong. Try again");
      }
      }
     
      rs.close(); s.close();c.close();

  }
  catch(Exception e){
      JOptionPane.showMessageDialog(this,e.getMessage());
  }
  }    // TODO add your handling code here:
}//GEN-LAST:event_jButton1ActionPerformed

